nta.declaration:
/**
N network nodes attempts to elect a leader by communicating witch each other.
The timeout deadline is dynamically calculated based on the number of hops a message do.
*/

const int N = 3;
const int M = 7;
const int MDELAY = 3;
const int TDELAY = 5;
const int TO = 10;

clock time;

typedef int[0,N-1] id_t;
typedef int[0,M-1] id_m_t;
typedef struct
{
  id_t src;
  id_t dst;
  id_t leader;
  int[0,N*N] hops;
} msg_t;

void setMsg(msg_t &msg, id_t src, id_t dst, id_t leader, int[0,N*N] hops)
{
  msg.src = src;
  msg.dst = dst;
  msg.leader = leader;
  msg.hops = hops;
}

chan send;
chan receive[N];
msg_t shared;

const int link[N][N] = {
  { 0,1,1 },
  { 1,0,1 },
  { 1,1,0 }
};

void reset(msg_t &msg)
{
  msg.src = msg.dst = msg.leader = msg.hops = 0;
}

bool used[M];

template.declaration:
//Insert local declarations of clocks, variables and constants.

clock x;
msg_t msg;

bool mayUse()
{
  int i = 0;
  while (i < id)
  {
    if(!used[i])
      return false;
    i++;
  }
  return true;
}

id_t leader = id;
int[0,N*N] hops;
clock x;
int[0,N] i;
id_t src;

void set(id_t l, int[0,N*N] h)
{
  leader = l;
  hops = h;
}

int[0,N] next(int[0,N] i,int[0,N] src)
{
  while (i  < N && (!link[id][i] || i == src))
  {
    i++;
  }
  return i;
}

int[0,1000] timeout()
{
  if (hops > 0)
    return TO + TDELAY + hops * MDELAY;
  return TO;
}

bool worse(const msg_t &msg)
{ 
  return msg.leader > leader || msg.leader == leader && msg.hops > hops;
}
