double mini(double d1, double d2){
    if(d1 < d2){return d1;}
    else{return d2;}
}

int eulerDiscrete(int vFront,int vEgo,int dist_int){
    //double velEgo, velFront, 
    double dist, delta, min, velEgo, velFront;
    int i;
    //int dist_int_g, dist_int_min;
    //int dist_int_min;
    velEgo = vEgo + 0.5;
    velFront = vFront + 0.5;
    dist = dist_int + 0.5;
    delta = 0.5;
    min = dist - delta;

    for (i=0;i<=euler_sub_step;i++){
        dist = dist + tau*(velFront - velEgo);
        velEgo = velEgo + tau*accelerationEgo;
        velFront = velFront + tau*accelerationFront;
        delta = delta_mode(delta,find_mode(accelerationFront,accelerationEgo));
        min = mini(min,dist-delta);
        //dist_int_min = integer_part(dist-delta);
        //dist_int_min = mini(dist_int_g,dist_int_min);
    }
    //dist_int_min = integer_part(min);
    //free(dist);
    return d2i(min);
}


void updateDiscrete(){
    int oldVel, newVel, oldVelFront, oldVelEgo;
    oldVel = velocityFront - velocityEgo;
    oldVelFront = velocityFront;
    oldVelEgo = velocityEgo;

    velocityEgo = velocityEgo + accelerationEgo;
    velocityFront = velocityFront + accelerationFront;

    newVel = velocityFront - velocityEgo;

    if (distance > maxSensorDistance) {
        distance = maxSensorDistance + 1;
    } else {
        distance_gua = eulerDiscrete(oldVelFront,oldVelEgo,distance);
        distance += (oldVel + newVel)/2;
       // distance = distance_gua;
    }
}



